mapper.select:
select
  id, member_id, create_time, change_count, note, source_type
from comm_ums.ums_growth_change_history


        and id = #{id}


        and member_id = #{memberId}


        and create_time = #{createTime}


        and change_count = #{changeCount}


        and note = #{note}


        and source_type = #{sourceType}
